import { onMounted } from "vue";
export default {
  name: "chessboard",

  setup() {
    onMounted(() => {
      // 画棋盘
      let container = document.getElementById("gobang");
      container.width = 800;
      container.height = 800;
      let ctx = container.getContext("2d"); //ctx.translate(70,70);

      ctx.beginPath();
      ctx.fillStyle = "#fff"; //ctx.rect(0, 0, 50, 50);

      ctx.fill();
      ctx.stroke();

      for (var i = 0; i < 15; i++) {
        ctx.beginPath();
        ctx.strokeStyle = "#D6D1D1";
        ctx.moveTo(15 + i * 45, 15); //垂直方向画15根线，相距30px;

        ctx.lineTo(15 + i * 45, 435);
        ctx.stroke();
        ctx.moveTo(15, 15 + i * 30); //水平方向画15根线，相距30px;棋盘为14*14；

        ctx.lineTo(435, 15 + i * 30);
        ctx.stroke(); //resultArr.push(new Array(15).fill(0));
      }
    });
  }

};
