import { onMounted } from "vue";
export default {
  name: "chessboard",

  setup() {
    let ctx = null;

    const getPointOnCanvas = function (canvas, x, y) {
      let bbox = canvas.getBoundingClientRect();
      return {
        x: x - bbox.left * (canvas.width / bbox.width),
        y: y - bbox.top * (canvas.height / bbox.height)
      };

      function doMouseDown(event) {
        var x = event.pageX;
        var y = event.pageY;
        var canvas = event.target;
        var loc = getPointOnCanvas(canvas, x, y);
        console.log("mouse down at point( x:" + loc.x + ", y:" + loc.y + ")");
        ctx.beginPath();
        ctx.moveTo(loc.x, loc.y);
        started = true;
      }
    };

    onMounted(() => {
      // 画棋盘
      let container = document.getElementById("gobang");
      container.width = 800;
      container.height = 800;
      container.addEventListener("mousedown", doMouseDown, false);
      ctx = container.getContext("2d"); //ctx.translate(70,70);

      ctx.beginPath();
      ctx.fillStyle = "#fff"; //ctx.rect(0, 0, 50, 50);

      ctx.fill();
      ctx.stroke();

      for (var i = 0; i < 15; i++) {
        ctx.beginPath();
        ctx.strokeStyle = "#D6D1D1";
        ctx.moveTo(15 + i * 45, 15); //垂直方向画15根线，相距45px;

        ctx.lineTo(15 + i * 45, 645);
        ctx.stroke();
        ctx.moveTo(15, 15 + i * 45); //水平方向画15根线，相距45px;棋盘为14*14；

        ctx.lineTo(645, 15 + i * 45);
        ctx.stroke(); //resultArr.push(new Array(15).fill(0));
      }
    });
  }

};
